mapper.update:
update wms_ware_sku
set stock=stock + #{skuNum}
where sku_id = #{id}
  and ware_id = #{wareId}

UPDATE wms_ware_sku
set stock_locked = stock_locked + #{num}
WHERE sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock - stock_locked >= #{num}

mapper.select:
SELECT SUM(stock - stock_locked)
from `wms_ware_sku`
WHERE sku_id = #{skuId}
